mapper.select:
select 

from statis_business
where business_type = #{businessType,jdbcType=SMALLINT}
  and business_id = #{businessId,jdbcType=BIGINT}
  and statis_item_key = #{statisItemKey,jdbcType=SMALLINT}

mapper.delete:
delete from statis_business
where business_type = #{businessType,jdbcType=SMALLINT}
  and business_id = #{businessId,jdbcType=BIGINT}
  and statis_item_key = #{statisItemKey,jdbcType=SMALLINT}

mapper.update:
update statis_business


    value = #{value,jdbcType=BIGINT},


    update_time = #{updateTime,jdbcType=TIMESTAMP},


where business_type = #{businessType,jdbcType=SMALLINT}
  and business_id = #{businessId,jdbcType=BIGINT}
  and statis_item_key = #{statisItemKey,jdbcType=SMALLINT}
